// components/ShopexOffer.tsx

import Image from "next/image";

export default function ShopexOffer() {
  const offers = [
    {
      id: 1,
      icon: "/icons/truck.png", // Replace with actual image paths
      title: "24/7 Support",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Massa purus gravida.",
    },
    {
      id: 2,
      icon: "/icons/coin.png", // Replace with actual image paths
      title: "24/7 Support",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Massa purus gravida.",
    },
    {
      id: 3,
      icon: "/icons/medal.png", // Replace with actual image paths
      title: "24/7 Support",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Massa purus gravida.",
    },
    {
      id: 4,
      icon: "/icons/clock.png", // Replace with actual image paths
      title: "24/7 Support",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Massa purus gravida.",
    },
  ];

  return (
    <section className="container mx-auto px-6 py-12">
      {/* Header */}
      <h2 className="text-center text-4xl font-bold text-indigo-900 mb-8">
        What Shopex Offer!
      </h2>

      {/* Grid Layout */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {offers.map((offer) => (
          <div
            key={offer.id}
            className="bg-white shadow-lg rounded-lg p-6 text-center"
          >
            {/* Icon */}
            <div className="flex justify-center mb-4">
              <Image
                src={offer.icon}
                alt={offer.title}
                width={60}
                height={60}
                className="object-contain"
              />
            </div>

            {/* Title */}
            <h3 className="text-lg font-semibold text-gray-800 mb-2">
              {offer.title}
            </h3>

            {/* Description */}
            <p className="text-gray-500 text-sm">{offer.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
