import React from 'react';
import { BiPhoneCall } from 'react-icons/bi';
import { GoPerson } from 'react-icons/go';
import { GrCart } from 'react-icons/gr';
import { IoIosHeartEmpty } from 'react-icons/io';
import { MdOutlineMailOutline } from 'react-icons/md';

const Top = () => {
  return (
    <div className="bg-[#7E33E0] h-[44px] w-full flex items-center justify-between px-96 lg:px-32 text-white">

  {/* Left Side */}
    <div className="flex items-center space-x-10">

    {/* Email */}
     <div className="flex items-center space-x-2">
       <MdOutlineMailOutline />
       <span className="text-sm">mhhasanul@gmail.com</span>
     </div>

    {/* Phone */}
     <div className="flex items-center space-x-2">
       <BiPhoneCall />
       <span className="text-sm">(12345)67890</span>
     </div>
   </div>


  {/* Right Side */}
      <div className="flex items-center space-x-8">
      {/* Language Dropdown */}
        <select className="bg-transparent px-3 py-1 rounded text-sm">
          <option value="english" className="text-black">English</option>
          <option value="urdu" className="text-black">Urdu</option>
        </select>

      {/* Currency Dropdown */}
        <select className="bg-transparent px-3 py-1 rounded text-sm">
          <option value="usd" className="text-black">USD</option>
          <option value="eur" className="text-black">EUR</option>
          <option value="gbp" className="text-black">GBP</option>
        </select>

      {/* Login */}
        <a href="/login" className="flex items-center text-sm hover:text-gray-300">
          <span className='mr-1'>Login</span>
          <GoPerson className='text-xl' />
        </a>

      {/* Wishlist */}
        <a href="/wishlist" className="flex items-center text-sm hover:text-gray-300">
          <span className='mr-1'>Wishlist</span>
          <IoIosHeartEmpty className='text-xl' />
        </a>

      {/* Cart */}
        <GrCart className='text-xl' />
      </div>
    </div>
  );
};

export default Top;
